import styled from 'styled-components';
import Image from 'next/image';

//Styled components
const Section = styled.section`
  max-width: 576px;
  margin: 0 auto 148px auto;
`;

const Title = styled.h2`
  font-size: 24px;
  margin-bottom: 16px;
`;

const Description = styled.p`
  color: ${(props) => props.theme.bodyText};
  font-size: 16px;
  line-height: 1.5;
  margin-bottom: 24px;
  white-space: pre-line;
`;

const Spacer = styled.div`
  width: 100%;
  height: 1px;
  border-radius: 1px;
  background-color: ${(props) => props.theme.bodyText};
  opacity: 0.12;
  margin-bottom: 24px;
`;

const SectionTitle = styled.p`
  color: ${(props) => props.theme.title};
  font-size: 16px;
  line-height: 1.5;
  margin-bottom: 24px;
  font-weight: 700;
`;

const CardContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 8px;
  margin-bottom: 24px;
`;

const CardLink = styled.a`
  text-decoration: none;
`;

const Card = styled.div`
  z-index: 1;
  display: flex;
  flex-direction: column;
  padding: 16px 16px 16px 16px;
  border-radius: 16px;
  background-color: ${(props) => props.theme.panel};
  border: 0.5px solid ${(props) => props.theme.border};

  &:hover {
    background-color: ${(props) => props.theme.panelHover};
    transition: background-color 0.2s ease;
  }
`;

const Tool = styled.div`
  display: flex;
  align-items: center;

  img {
    height: 24px;
    width: 24px;
    border-radius: 4px;
  }

  h3 {
    margin-left: 16px;
    font-size: 16px;
    color: ${(props) => props.theme.titleColor};
    font-weight: 400;
  }
`;

const Info = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const OpenSource = styled.div`
  height: 24px;
  font-size: 14px;
  background: ${(props) => props.theme.tag};
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 8px 1px 8px;
  color: ${(props) => props.theme.bodyText};
  border-radius: 4px;
  line-height: 1.5;
  margin-left: 8px;
`;

const Discount = styled.a`
  display: flex;
  font-size: 14px;
  align-items: center;
  padding: 12px;
  margin-top: 16px;
  background-color: #55acee24;
  border: 0.5px solid #55acee;
  color: #3291ff;
  border-radius: 8px;
  text-decoration: none;

  svg {
    stroke: #3291ff;
    margin-right: 8px;
  }
`;

const Devices = styled.div`
  display: flex;
  align-items: center;

  @media (max-width: 992px) {
    display: none;
  }
`;

const DeviceActive = styled.span`
  width: 24px;
  height: 24px;
  margin-left: 8px;

  svg {
    fill: ${(props) => props.theme.bodyText};
    width: 100%;
    height: 100%;
  }
`;

const DeviceDisabled = styled.span`
  width: 24px;
  height: 24px;
  margin-left: 8px;
  opacity: 0.24;

  svg {
    fill: ${(props) => props.theme.bodyText};
    width: 100%;
    height: 100%;
  }
`;

const ArticleContainer = styled.div`
  display: grid;
  grid-template-columns: 1fr;
  gap: 8px;
`;

const Article = styled.a`
  cursor: pointer;
  display: flex;
  font-size: 14px;
  align-items: center;
  padding: 16px;
  background-color: ${(props) => props.theme.panel};
  border: 0.5px solid ${(props) => props.theme.border};
  color: ${(props) => props.theme.bodyText};
  border-radius: 16px;
  text-decoration: none;

  &:hover {
    background-color: ${(props) => props.theme.panelHover};
    transition: background-color 0.2s ease;
  }

  svg {
    stroke: ${(props) => props.theme.titleColor};
    margin-right: 8px;
  }
`;

